import { useEffect, useState } from "react";
import { Link, Navigate, useParams } from "react-router-dom";

import './style.css'

function Home(params) {

    const token = localStorage.getItem('access_token');
    const [homes, setHomes] = useState({});
    const { homenum } = useParams();
    let url = params.url + "homes/"
    if (homenum)
        url += homenum + '/';
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => res.json()).then(res => {
                console.log(res);
                setHomes(res);
            })
        }
    },[url, token])



    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }


    return (

        <div className="home-container mt-4">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes.homes && homes.homes.map((item, i) => {
                    return (<p><Link to={"/" +item.id} >{item.name}</Link></p>)
                })}
            </div>
            <div className="rooms">
                {homes.rooms && homes.rooms.map((item, i) => {
                    return (<p><Link to={homenum + "/" + item.id}>{item.name}</Link></p>)
                })}
            </div>

            <div className="lights">
                {homes.lights && homes.lights.map((item, i) => {
                    return (<p>{item.name}</p>)
                })}
            </div>
        </div>
    );
}

export default Home;
