import { useEffect, useState } from "react";
import { Navigate, useParams } from "react-router-dom";


function Edit(params) {
    const { hid, rid, lid } = useParams();
    const [element, setElement] = useState({});
    const token = localStorage.getItem('access_token');
    let url = params.url;
    if (hid) {
        url += hid + "/"
    }
    if (rid) {
        url += rid + "/"
    }
    if (lid) {
        url += lid + "/"
    }
    url += "edit/";

    console.log(url)
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {

                    res = res.json();

                    if (lid) {
                        setElement(res.light);
                    }
                    else if (rid) {
                        setElement(res.room);
                    }
                    else if (hid) {
                        setElement(res.home);
                    }
                }
            });
        }
    });
    if (!token) {
        return <Navigate to='login'/>
    }
    return (<div>
        <h1>Do You really Want to Edit {element && element.name} ??</h1>
    </div>)
}

export default Edit;
