import { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import { Link, Navigate, useParams } from "react-router-dom";

import './style.css'

function Home(params) {

    const token = localStorage.getItem('access_token');
    const [homes, setHomes] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [lights, setLights] = useState([]);
    const { homenum,roomnum } = useParams();
    let url = params.url + "homes/"
    if (homenum)
        url += homenum + '/';
    if (roomnum)
        url+=roomnum+"/"
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => res.json()).then(res => {
                setHomes(res.homes);
                if (homenum) {
                    setRooms(res.rooms);
                }
                if (roomnum) {
                    setLights(res.lights);
                }
            })
        }
    }, [url, token])


    async function changeState(item,index) {


        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: localStorage.getItem('access_token')
            }
        }).then(res => res.json()).then(res => {

            item = res.light;
            var data = lights;
            data[index] = item;
            setLights(data)
        })
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }



    return (

        <div className="home-container mt-4">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((item, i) => {
                    return (<p><Link to={"/" +item.id} >{item.name}</Link></p>)
                })}
            </div>
            <div className="rooms">
                {rooms && rooms.map((item, i) => {
                    return (<p><Link to={"/"+homenum + "/" + item.id}>{item.name}</Link></p>)
                })}
            </div>

            <div className="lights">
                {lights && lights.map((item, i) => {
                    return (<Button variant={item.state ? "success" : "danger"} id={"light-"+item.id} onClick={()=> changeState(item,i)}>{item.name}</Button>)
                })}
            </div>
        </div>
    );
}

export default Home;
