import { useState } from "react";
import { useMessageContext } from "../../context/messageprovider";



function Light(params) {
    const roomid = params.room;
    const token = localStorage.getItem('access_token');

    const [lightedit, setLightedit] = useState(-1);
    const [lights, setLights] = useState([]);
    const [owner,setOwner] = useState("")

    const msgcontext = useMessageContext();

    let url = `${params.url}rooms/${roomid}/`;
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {
                    res.json().then(res => {
                        setLights(res.lights);
                        setOwner(res.owner);
                    })
                }
                else {
                    msgcontext.append({ message: "Error Loading Lights", type: 'error' });
                }
            });
        }
    }, [url, token,roomid])

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }


    function editLight(light) {
        setLightedit(light.id);
    }

    async function handleLightSubmit(e, light) {

    }

    function handleDeleteLight(light) {

    }

    function cancelDelete(light) {

    }

    async function confirmLightDelete(light) {

    }

    function handleLightChange(index, e) {

    }

    return (
        lights && lights.map((light, i) => {
            lightedit === light.id ?
                <form onSubmit={(e) => handleLightSubmit(e, light)}>
                    <div className="light-container">
                        <input className="input-edit room-title" type={'text'} value={light.name} onChange={(e) => handleLightChange(i, e)} />
                        <a className="post-btn" onClick={(e) => handleDeleteLight(light)}><i className="fa fa-xmark fa-danger"></i></a>
                        <button className="post-btn"><i className="fa fa-check fa-success"></i></button>
                    </div>
                </form>
                :
                <div className="light-container">
                    <button className={"light-title light-" + (light.state ? "active" : "not-active")} onClick={() => changeState(light, i)}><span>{light.name}</span><i className={"fa-lightbulb fa " + (light.state ? "fa-solid fa-success" : "fa-regular fa-danger")}></i></button>
                    {owner === user_id && <a className="btn-edit" onClick={()=>editLight(light) }><i className="fa fa-gear fa-primary"></i></a>}
                </div>
        })
    )
}

export default Light;
