import { createContext, useContext, useState } from "react";


const usercontext = createContext();

function UserProvider({ children }) {
    const tokenname = "authTokens";
    const [user, setUser] = useState(() => { localStorage.getItem(tokenname) ? JSON.parse(localStorage.getItem(tokenname)) : null });
    const [token, setToken] = useState(() => {
        localStorage.getItem(tokenname) ? JSON.parse(localStorage.getItem(tokenname)) : null;
    })
    const loginUser = (res) => {
        setUser(res);
        localStorage.setItem(tokenname, JSON.stringify(res));
        setToken(res);
    }

    return (
        <usercontext.Provider value={{ 'user': user, 'login': loginUser, 'token':token }}>
            {children}
        </usercontext.Provider>
    )
}

export const useUserContext = () => useContext(usercontext);

export default UserProvider;
