function Roomitem(params) {

    function handleDeleteRoom(room) {
        params.changedeleting(room.id);
    }

    function cancelRoomDelete() {
        params.changedeleting(-1);
    }

    async function confirmRoomDelete(e, room){
        await fetch(`${params.url}delete/room/${room.id}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {

                params.changedeleting(-1);
                setHomeedit(-1);
                msgcontext.append({ message: res.message, type: status === 200 ? 'success' : 'error' })
            });
        });
    }

    return (
        params.isdeleting == params.room.id ?
            <div className="room delete">
                <label>Confirm?</label>
                <a className="post-btn" onClick={(e)=>confirmRoomDelete(e,params.room)}><i className="fa fa-check fa-danger"></i></a>
                <a className="post-btn" onClick={cancelRoomDelete}><i className="fa fa-xmark fa-success"></i></a>
            </div> :
            <div className="room">
                <input className="input-edit room-title" type={'text'} value={params.room.name} onChange={(e) => handleRoomChange(params.hidx, params.ridx, e)} />
                <a className="post-btn" onClick={() => handleDeleteRoom(params.room)}><i className="fa fa-xmark fa-danger"></i></a>
            </div>
    )
}

export default Roomitem;
