import "./style.css"

function Search(params) {
    const user_id = localStorage.getItem('user_id');
    const token = localStorage.getItem('access_token');

    async function sendrequest(e, home) {
        await fetch(`${params.url}sedrequest/${home.id}`,{
            method: 'GET',
            headers:{
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res=>{
            const status = res.status;
            res.json().then(res=>{
                msgcontext.append(res.message,status);
            })
        })
    }

    async function cancelRequest(e, home) {
        await fetch(`${params.url}removerequest/${home.id}`,{
            method: 'GET',
            headers:{
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res=>{
            const status = res.status;
            res.json().then(res=>{
                msgcontext.append(res.message,status);
            })
        })
    }



    return <div className="search container">
        {params.results.users && params.results.users.map((user) => {
            return (
                <div className="user">
                    <div className="userinfo">
                        <h1 className="username">{user.username}</h1>
                        <h3 className="name">{user.name}</h3>
                    </div>
                    <div className="homes">
                        {user.homes.map((home) => {
                            return (
                                <div className="home">
                                    <p>{home.name}</p>
                                    <div className="options">
                                        {(user_id && home.requests.includes(user_id)) ?
                                            <div className="remove">
                                                <span className="option-label">Remove</span> 
                                                <i className="fa fa-xmark fa-danger"></i>
                                            </div> :
                                            <div className="join">
                                                <span className="option-label">Join</span> 
                                                <i className="fa fa-check fa-success"></i>
                                            </div>
                                            }
                                    </div>
                                </div>
                            )
                        })}
                    </div>
                </div>
            )
        })}
    </div>;
}

export default Search;
