import { useEffect, useState } from "react";
import { Link, Navigate, useParams } from "react-router-dom";

import "./style.css"

function Home(params) {

    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));
    const [homes, setHomes] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [lights, setLights] = useState([]);
    const [owner, setOwner] = useState("");
    const { homenum,roomnum } = useParams();
    let url = params.url + "homes/"
    if (homenum)
        url += homenum + '/';
    if (roomnum)
        url+=roomnum+"/"
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => res.json()).then(res => {

                if (roomnum) {
                    setLights(res.lights);
                }
                else if(!homes) {
                    setHomes(res.homes);
                    // console.log(res.homes)
                }
            })
        }
    }, [url, token,homenum,roomnum])


    async function changeState(item,index) {

        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }



    return (
        <>

        <div className="home-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((item, i) => {
                    return (
                        <div className={"tile-btn " + (homenum == item.id && "active")}>
                            <h2>{item.name}</h2>
                            {item.owner === user_id && <a className="btn-edit" href={"/" + item.id + "/edit"}><i className="fa fa-gear"></i></a>}
                            { item.rooms.map((room, i) => {
                                return (
                                    <div className={'room '+(room.id == roomnum ? 'active':"")}>
                                        <Link to={`/${item.id}/${room.id}`}>
                                            <p>{room.name}</p>
                                        </Link>
                                    </div>
                                )
                            })}
                            <hr/>
                        </div>)
                })}

            </div>
            <div className="light-container">
                <div className="lights">
                    {lights && lights.map((item, i) => {
                        return (
                            <div className="tile-btn">
                                <button className={"card btn-"+ (item.state ? "active" : "not-active")} onClick={()=> changeState(item,i)}>
                                    <div className='card-title'>
                                            {item.name}
                                    </div>
                                </button>
                                {owner === user_id && <a className="btn-edit" href={`/${homenum}/${roomnum}/${item.id}/edit`}><i className="fa fa-gear"></i></a>}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
