import { useEffect, useState } from "react";
import { useMessageContext } from "../../context/messageprovider";
import "./style.css"


function Light(params) {
    const roomid = params.room;
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));

    const [lightedit, setLightedit] = useState(-1);
    const [lights, setLights] = useState([]);
    const [deletinglight, setDeletinglight] = useState(-1);
    const [owner,setOwner] = useState("")
    const [createbulb, setCreatebulb] = useState(false);

    let options = []
    for (let i = 0; i < 10; i++){
        options.push(<option value={i}>{i}</option>);
    }


    const msgcontext = useMessageContext();

    let url = `${params.url}rooms/`;
    if (roomid)
        url += `${roomid}/`;

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {
                    res.json().then(res => {
                        setLights(res.lights);
                        setOwner(res.owner);
                    })
                }
                else {
                    msgcontext.append({ message: "Error Loading Lights", type: 'error' });
                }
            });
        }
    }, [url, token,roomid,lightedit])

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }

    async function handleLightSubmit(e, light) {
        e.preventDefault();
        await fetch(`${params.url}edit/light/${light.id}/`, {
            method: "POST",
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            },
            body: JSON.stringify({ 'light': light })
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, status);
            });
        });
        setLightedit(-1);
    }

    async function confirmLightDelete(light) {
        await fetch(`${params.url}delete/light/${light.id}/`, {
            method: 'GET',
            headers:{
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, status);
            });
        })
        setLightedit(-1);
    }

    function handleLightChange(index, e) {
        let newlights = [...lights];
        let light = newlights[index];
        light.name = e.target.value;
        newlights[index] = light;
        setLights(newlights);
    }

    async function createLight(e) {
        e.preventDefault();
        const formdata = new FormData(e.currentTarget);
        await fetch(`${params.url}create/light/${roomid}/`, {
            method: 'POST',
            body: JSON.stringify({ 'room': parseInt(roomid), 'light': { 'name': formdata.get('name'), 'port': formdata.get('port') } }),
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, status);
            })

        })
        setCreatebulb(false);
    }

    return (
            lights && lights.map((light, i) => {
                <div key={"light-" + light.id}>
                    {lightedit === parseInt(light.id) ?
                        deletinglight === parseInt(light.id) ?
                            <form onSubmit={() => confirmLightDelete(light)}>
                                <div className="light-container">
                                    <label>Confirm ?</label>
                                    <div className="buttons-form">
                                        <div className="post-btn light-edit-btn" onClick={() => confirmLightDelete(light)}><i className="fa fa-check fa-danger"></i></div>
                                        <div className="post-btn light-edit-btn" onClick={() => setDeletinglight(-1)}><i className="fa fa-xmark fa-success"></i></div>
                                    </div>
                                </div>
                            </form> :
                            <form onSubmit={(e) => handleLightSubmit(e, light)}>
                                <div className="light-container">
                                    <input className="input-edit room-title" type={'text'} value={light.name} onChange={(e) => handleLightChange(i, e)} />
                                    <div className="buttons-form">
                                        <div className="post-btn" onClick={(e) => setDeletinglight(light.id)}><i className="fa fa-xmark fa-danger fa-bigger"></i></div>
                                        <div className="post-btn" onClick={(e) => handleLightSubmit(e, light)}><i className="fa fa-check fa-success fa-bigger"></i></div>
                                    </div>
                                </div>
                            </form >
                        :
                        <div className="light-container">
                            <div className={"light-title light-" + (light.state ? "active" : "not-active")} onClick={() => changeState(light, i)}><i className={"fa-lightbulb fa " + (light.state ? "fa-solid fa-success-darker" : "fa-regular fa-danger-darker")}></i><span>{light.name}</span></div>
                            {owner === user_id && <div className="btn-edit" onClick={() => setLightedit(light.id)}><i className="fa fa-smaller fa-gear fa-primary"></i></div>}
                        </div>
                    }
                </div>
                })

    )
}

export default Light;
