import { useEffect, useState } from "react";
import { useMessageContext } from "../../context/messageprovider";



function Light(params) {
    const roomid = params.room;
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));

    const [lightedit, setLightedit] = useState(-1);
    const [lights, setLights] = useState([]);
    const [deletinglight, setDeletinglight] = useState(-1);
    const [owner,setOwner] = useState("")

    const msgcontext = useMessageContext();

    let url = `${params.url}rooms/${roomid}/`;

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {
                    res.json().then(res => {
                        console.log(res);
                        setLights(res.lights);
                        setOwner(res.owner);
                    })
                }
                else {
                    msgcontext.append({ message: "Error Loading Lights", type: 'error' });
                }
            });
        }
    }, [url, token,roomid,lightedit])

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }

    function editLight(light) {
        setLightedit(light.id);
    }

    async function handleLightSubmit(e, light) {
        await fetch(`${params.url}edit/light/${light.id}/`, {
            method: "POST",
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            },
            body: JSON.stringify({ 'light': light })
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, status);
            });
        });
        setLightedit(-1);
    }

    function handleDeleteLight(light) {
        setDeletinglight(light.id);
    }

    function cancelDelete(light) {

    }

    async function confirmLightDelete(light) {

    }

    function handleLightChange(index, e) {
        let newlights = [...lights];
        let light = newlights[index];
        light.name = e.target.value;
        newlights[index] = light;
        setLights(newlights);
    }

    return (
        lights && lights.map((light, i) => {
            return (
                lightedit === parseInt(light.id) ? 
                    deletinglight === parseInt(light.id) ? 
                        <form onSubmit={() => confirmLightDelete(light)}>
                            <label>Confirm ?</label>
                            <button className="post-btn light-edit-btn"><i className="fa fa-check fa-danger"></i></button>
                            <a className="post-btn light-edit-btn" onClick={() => cancelDelete(light)}><i className="fa fa-xmark fa-success"></i></a>
                        </form> :
                        <form onSubmit={(e) => handleLightSubmit(e, light)}>
                            <div className="light-container">
                                <input className="input-edit room-title" type={'text'} value={light.name} onChange={(e) => handleLightChange(i, e)} />
                                <a className="post-btn light-edit-btn" onClick={(e) => handleDeleteLight(light)}><i className="fa fa-xmark fa-danger"></i></a>
                                <button className="post-btn light-edit-btn"><i className="fa fa-check fa-success"></i></button>
                            </div>
                        </form >
                :
                <div className="light-container">
                    <button className={"light-title light-" + (light.state ? "active" : "not-active")} onClick={() => changeState(light, i)}><span>{light.name}</span><i className={"fa-lightbulb fa " + (light.state ? "fa-solid fa-success" : "fa-regular fa-danger")}></i></button>
                    {owner === user_id && <a className="btn-edit" onClick={()=>editLight(light) }><i className="fa fa-gear fa-primary"></i></a>}
                </div>
            )
        })
    )
}

export default Light;
