import { useEffect, useState } from "react"
import { Button, Form } from "react-bootstrap"


function Login(params) {

    const [form, setform] = useState("")
    const [loading, setloading] = useState({})



    const senddata = (event) =>{
        const getuser = async () => {
            console.log("something called");
            const formdata = event.currentTarget.json();
            setloading(true);
            await fetch(params.url + "login/", {
                method: "POST",
                Body: formdata
            })
                .then(res => res.json())
                .then(res => {
                    console.log(res);
                    setform(res);
            })
        }
        console.log("Send data called;")
        getuser();
    }

    return (
        <>
            <Form onSubmit={senddata}>
                <Form.Group className="mb-3" >
                    <Form.Label>Username</Form.Label>
                    <Form.Control type='text' name="username" placeholder="Enter Username" required/>
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type='password' name="password" placeholder="Enter Password" required/>
                </Form.Group>

                <Button variant="success" type="submit">
                    Login
                </Button>

                <div className="error">{form}</div>
            </Form>
        </>
    )
}

export default Login;
