import { useEffect, useState } from "react";
import { Form, Button } from "react-bootstrap";
import { Navigate, useParams } from "react-router-dom";


function Edit(params) {
    const { hid, rid, lid } = useParams();
    const [element, setElement] = useState({});
    const token = localStorage.getItem('access_token');
    let url = params.url;
    if (lid) {
        url += 'light/' + lid + "/";
    }
    else if (rid) {
        url += 'room/' + rid + "/";
    }
    else if (hid) {
        url += 'home/' + hid + "/";
    }

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {

                    res = res.json()
                    .then(data => {
                        if (lid) setElement(data.light);
                        else if (rid) setElement(data.room);
                        else if (hid) setElement(data.home);
                    })
                }
            })
        }
    },[token,hid,rid,lid]);

    function change(e) {
        const form = e.currentTarget;
        console.log(form);
    }

    function changeName(e) {
        setElement({ id: element.id, name: e.target.value });
        console.log(e.target.value);
    }

    if (!token) {
        return <Navigate to='login'/>
    }
    return (<div>
        <h1>Do You really Want to Edit {element && element.name} ??</h1>
        <Form onSubmit={change}>
            <Form.Group className="mb-3">
                <Form.Label>Name</Form.Label>
                    <Form.Control type='text' name="name" placeholder="Enter Name" value={element.name} onChange={changeName} required/>
            </Form.Group>
                <Button className="mt-4" variant="success" type="submit">
                    Edit
                </Button>
        </Form>
    </div>)
}

export default Edit;
