import { useEffect, useState } from "react";
import { Link, Navigate, useParams } from "react-router-dom";
import { useMessageContext } from "../../context/messageprovider";
import Light from "./lightpage";
import "./style.css"

function Home(params) {
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));
    const { roomnum } = useParams();

    const msgcontext = useMessageContext()

    const [homes, setHomes] = useState([]);
    const [owner, setOwner] = useState("");
    const [homeedit, setHomeedit] = useState(-1);
    const [deletingHome, setDeletingHome] = useState(-1);
    const [deletingRoom, setDeletingRoom] = useState(-1);

    let url = params.url + "rooms/"

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {
                    res.json().then(res => {
                        setHomes(res.homes);
                        setOwner(res.owner);
                    })
                }
                else if(res.status == 404) {
                    localStorage.removeItem('access_token');
                    localStorage.removeItem('user_id');
                    token = localStorage.getItem('access_token');
                    user_id = parseInt(localStorage.getItem('user_id'));
                }
            });
        }
    }, [url, token,roomnum,homeedit])



    function editHome(id,status) {
        if (status) {
            setHomeedit(id);
        }
        else {
            setHomeedit(-1);
        }
    }



    function handleHomeChange(index, e) {
        let newHomes = [...homes];
        let home = newHomes[index];
        home.name = e.target.value;
        newHomes[index] = home;
        setHomes(newHomes);
    }
    function handleRoomChange(homeIndex, roomIndex, e) {
        let newHomes = [...homes];
        let home = newHomes[homeIndex];
        let room = home.rooms[roomIndex];
        room.name = e.target.value;
        home.rooms[roomIndex] = room;
        newHomes[homeIndex] = home;
        setHomes(newHomes);
    }


    async function handleHomeSubmit(event, home) {
        event.preventDefault();
        await fetch(`${params.url}edit/home/${home.id}/`, {
            method: 'POST',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            },
            body: JSON.stringify({ 'home': home })
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message,status);
            })
            setHomeedit(-1);
        })
    }


    function handleDeleteRoom(room) {
        setDeletingRoom(room.id);
    }
    function cancelRoomDelete() {
        setDeletingRoom(-1);
    }
    async function confirmRoomDelete(e, room){
        await fetch(`${params.url}delete/room/${room.id}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {

                setDeletingRoom(-1);
                setHomeedit(-1);
                msgcontext.append(res.message, status);
            });
        });
    }

    function handleDeleteHome(home) {
        setDeletingHome(home.id);
    }
    function cancelHomeDelete() {
        setDeletingHome(-1);
    }
    async function confirmHomeDelete(e, home) {
        await fetch(`${params.url}delete/home/${home.id}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, status);
            });
        });
        setDeletingHome(-1);
    }

    async function createRoom(e, home) {

        await fetch(`${params.url}createroom/${home.id}/`, {
            method: 'POST',
            body: JSON.stringify({ hello: 'hello' }), 
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => {
            const stat = res.status;
            res.json().then(res => {
                msgcontext.append(res.message, stat);
            })
        })
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }

    return (
        <>
            <div className="main-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((home, homeIndex) => {
                    return (<>{homeedit === home.id ?
                        deletingHome === home.id ?
                            <div>
                                <label className="home-label">Confirm Delete ?</label>
                                <div className="buttons-form buttons-home-delete">
                                    <div className="push-btn" onClick={cancelHomeDelete}><i className="fa fa-xmark fa-success"></i></div>
                                    <div className="push-btn" onClick={(e)=>confirmHomeDelete(e,home)}><i className="fa fa-check fa-danger"></i></div>
                                </div>
                            </div> :
                            <form className="form-home-edit" onSubmit={(e)=>handleHomeSubmit(e,home)}>
                                <div className="home-container">
                                    <input className="input-edit home-title" type={'text'} value={home.name} onChange={(e) => handleHomeChange(homeIndex, e)} />
                                    <div className="buttons-form">
                                        <div className="post-btn" onClick={()=>handleDeleteHome(home)}><i className="fa fa-xmark fa-bigger fa-danger"></i></div>
                                        <div className="post-btn" onClick={(e)=>handleHomeSubmit(e,home)}><i className="fa fa-check fa-bigger fa-success"></i></div>
                                    </div>
                                </div>
                                <div className="edit-mode room-container">
                                    {home.rooms && home.rooms.map((room, roomIndex) => {
                                        return (
                                            deletingRoom === parseInt(room.id) ?
                                                <div className="room delete">
                                                    <label>Confirm?</label>
                                                    <div className="buttons-form buttons-room-delete">
                                                        <div className="post-btn" onClick={(e)=>confirmRoomDelete(e,room)}><i className="fa fa-check fa-danger"></i></div>
                                                        <div className="post-btn" onClick={cancelRoomDelete}><i className="fa fa-xmark fa-success"></i></div>
                                                    </div>
                                                </div> :
                                                <div className="room">
                                                    <input className="input-edit room-title" type={'text'} value={room.name} onChange={(e) => handleRoomChange(homeIndex, roomIndex, e)} />
                                                    <div className="post-btn" onClick={() => handleDeleteRoom(room)}><i className="fa fa-xmark fa-danger"></i></div>
                                                </div>
                                        )
                                    })}
                                    <form >
                                        <input className="input-edit room-title" type={'text'} placeholder='room Name' />
                                        <button className="post-btn"><i className="fa fa-plus fa-success fa-bigger"></i></button>
                                    </form>
                                </div>
                            </form> :
                        <div className={"home-tile "}>
                            <div className="home-container">
                                <p className="home-title">{home.name}</p>
                                {home.owner === user_id && <div className="btn-edit" onClick={() => editHome(home.id, true)}><i className="fa fa-gears fa-primary"></i></div>}
                            </div>
                            <div className="room-container">
                                {home.rooms && home.rooms.map((room, i) => {
                                    return (
                                        <div className='room'>
                                            <Link to={`/${room.id}`}>
                                                <p className={"room-title " + (room.id.toString() === roomnum ? 'active' : "")}>{room.name}</p>
                                            </Link>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    }
                    <hr/></>)
                })}

            </div>
            <div className="light-tile">
                <div className="lights">
                        <Light room={roomnum} url={params.url}/>
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
