import { useEffect, useState } from "react";
import { Link, Navigate, redirect, useParams } from "react-router-dom";

import "./style.css"

function Home(params) {
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));

    const { roomnum } = useParams();

    const [homes, setHomes] = useState([]);
    const [lights, setLights] = useState([]);
    const [owner, setOwner] = useState("");
    const [homeedit, setHomeedit] = useState(-1);

    let url = params.url + "rooms/"
    if (roomnum)
        url += roomnum + "/"

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status == 200) {
                    res.json().then(res => {
                        setHomes(res.homes);
                        if (roomnum) {
                            setLights(res.lights);
                        }
                        setOwner(res.owner);
                    })
                }
            });
        }
    }, [url, token,roomnum])


    function editHome(id,status) {
        if (status) {
            setHomeedit(id);
        }
        else {
            setHomeedit(-1);
        }
    }

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }

    function handleHomeChange(index, e) {
        let newHomes = [...homes];
        let home = newHomes[index];
        home.name = e.target.value;
        newHomes[index] = home;
        setHomes(newHomes);
    }

    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }

    return (
        <>
            <div className="main-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((home, i) => {
                    return (<>{homeedit === home.id ?
                        <form className="form-home-edit">
                            <div className="home-container">
                                <input type={'text'} value={home.name} onChange={(e) => handleHomeChange(i, e)} />
                                <input type={'submit'} value={<i className="fa fa-check"></i>}/>
                            </div>
                        </form> :
                        <div className={"home-tile "}>
                            <div className="home-container">
                                <p className="home-title">{home.name}</p>
                                {home.owner === user_id && <a className="btn-edit" onClick={() => editHome(home.id, true)}><i className="fa fa-gears"></i></a>}
                            </div>
                            <div className="rooms-container">
                                {home.rooms && home.rooms.map((room, i) => {
                                    return (
                                        <div className='room'>
                                            <Link to={`/${room.id}`}>
                                                <p className={"room-title " + (room.id == roomnum ? 'active' : "")}>{room.name}</p>
                                            </Link>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    }

                    <hr/></>)
                })}

            </div>
            <div className="light-container">
                <div className="lights">
                    {lights && lights.map((item, i) => {
                        return (
                            <div >
                                <button className={"hello"+ (item.state ? "active" : "not-active")} onClick={()=> changeState(item,i)}>
                                    <div className='card-title'>
                                            {item.name}
                                    </div>
                                </button>
                                {owner === user_id && <a className="btn-edit" href={`/${roomnum}/${item.id}/edit`}><i className="fa fa-gear"></i></a>}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
