import { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import { Link, Navigate, useParams } from "react-router-dom";

import './style.css'

function Home(params) {

    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));
    const [homes, setHomes] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [lights, setLights] = useState([]);
    let owner = "";
    const { homenum,roomnum } = useParams();
    let url = params.url + "homes/"
    if (homenum)
        url += homenum + '/';
    if (roomnum)
        url+=roomnum+"/"
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => res.json()).then(res => {
                console.log(res);
                setHomes(res.homes);
                if (homenum) {
                    setRooms(res.rooms);
                    owner = res.owner;
                }
                if (roomnum) {
                    setLights(res.lights);
                }
            })
        }
    }, [url, token,homenum,roomnum])


    async function changeState(item,index) {

        console.log(item.state);
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: localStorage.getItem('access_token')
            }
        }).then(res => res.json()).then(res => {


            const newlights = res.lights;
            setLights(newlights);
        })
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }



    return (

        <div className="home-container mt-4">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((item, i) => {
                    return (<>
                        <p><Link to={"/" + item.id} >{item.name}</Link></p>
                        {item.owner === user_id && <p><Link to={"/" + item.id + "/edit/"}>Edit</Link></p>}
                    </>)
                })}
            </div>
            <div className="rooms">
                {rooms && rooms.map((item, i) => {
                    return (<>
                    <p><Link to={"/" + homenum + "/" + item.id}>{item.name}</Link></p>
                    {owner === user_id && <p><Link to={"/" + homenum + "/" + item.id+"/edit/"}>Edit</Link></p>}
                    </>)
                })}
            </div>

            <div className="lights">
                {lights && lights.map((item, i) => {
                    return (<><Button variant={item.state ? "active" : "not-active"} onClick={() => { changeState(item, i); }}>{item.name}</Button>
                        {owner === user_id && <p><Link to={"/" + homenum + "/" + roomnum + "/" + item.id + "/edit/"}>Edit</Link></p> }</>)
                })}
            </div>
        </div>
    );
}

export default Home;
