import { useState } from "react"
import { Button, Form, Stack,Spinner } from "react-bootstrap"
import { Navigate } from "react-router-dom"
import { useUserExistContext } from "../../context/userloggedIn";


function Login(params) {

    const [redirect, setRedirect] = useState(false);
    const [loading, setLoading] = useState(false);
    const userContext = useUserExistContext();

    const senddata = async (event) => {
        setLoading(true)
        event.preventDefault();
        const form = event.target;
        const formdata = new FormData(form);
        const actualdata = {
            username: formdata.get('username'),
            password: formdata.get('password')
        }
        await fetch(params.url + "login/", {

            method: "POST",
            headers: {
                'Content-type': 'application/json',
            },
            body: JSON.stringify(actualdata),
        }).then(res => res.json()).then(data => {
            if (data.token) {
                localStorage.setItem("access_token", data.token);
                localStorage.setItem('user_id', data.user_id)
                userContext.set(true);
                setRedirect(true);
            }
        });
        setLoading(false);
    }


    return (
        <>
            {redirect && <Navigate to="/"/>}
            <Form className="login-form" onSubmit={senddata}>
                <Stack gap={2} className='col-md-5 mx-auto'>
                    <Form.Group className="mb-3" >
                        <Form.Label>Username</Form.Label>
                        <Form.Control type='text' name="username" placeholder="Enter Username" required/>
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type='password' name="password" placeholder="Enter Password" required/>
                    </Form.Group>

                    <Button variant="success" type="submit">
                        {loading ? <Spinner
                            as="span"
                            animation="grow"
                            size="md"
                            role="status"
                            aria-hidden="true"
                        /> : Login}
                    </Button>
                    </Stack>
            </Form>


        </>
    )
}

export default Login;
