import { useEffect, useState } from "react";
import { Form, Button } from "react-bootstrap";
import { Navigate, useParams } from "react-router-dom";


function Edit(params) {
    const { hid, rid, lid } = useParams();
    const [element, setElement] = useState({});
    const [eleType, setEletype] = useState("");
    const [redirect, setRedirect] = useState({ redirect: false, message: '', type: '' });
    const token = localStorage.getItem('access_token');
    let url = params.url;
    if (lid) {
        url += 'light/' + lid + "/";
    }
    else if (rid) {
        url += 'room/' + rid + "/";
    }
    else if (hid) {
        url += 'home/' + hid + "/";
    }

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {

                    res = res.json()
                    .then(data => {
                        if (lid) {
                            setElement(data.light);
                            setEletype("Light");
                        }
                        else if (rid) {
                            setElement(data.room);
                            setEletype("Room");
                        }
                        else if (hid) {
                            setElement(data.home);
                            setEletype("Home");
                        }
                    })
                }
            })
        }
    },[token,hid,rid,lid,url]);

    function handleSubmit(e) {
        e.preventDefault();
        fetch(url + 'edit/', {
            method: 'POST',
            headers: {
                Authorization: token
            },
            body: JSON.stringify(element)
        }).then(res => {
            if (res.status === 200) {
                res.json()
                    .then(res => {
                        setRedirect({redirect:true, message:res.message, type:'success'});
                })
            }
            else {
                setRedirect({ redirect: true, message: res.message, type: 'error' });
            }
        })

    }

    function handleChange(e) {
        setElement({ id: element.id, name: e.target.value });
        console.log(e.target.value);
    }

    if (!token) {
        return <Navigate to='login'/>
    }
    return (<div>
        {redirect.redirect && <Navigate to=""/>}
        <h1>Change Name of {eleType} to "{element && element.name}" ?</h1>
        <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
                <Form.Label>Name</Form.Label>
                    <Form.Control type='text' name="name" placeholder="Enter Name" value={element.name} onChange={handleChange} required/>
            </Form.Group>
                <Button className="mt-4" variant="success" type="submit">
                    Edit
                </Button>
        </Form>
    </div>)
}

export default Edit;
