import { useEffect, useState } from "react";
import { Link, Navigate, redirect, useParams } from "react-router-dom";

import "./style.css"

function Home(params) {
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));

    const { roomnum } = useParams();

    const [homes, setHomes] = useState([]);
    const [lights, setLights] = useState([]);

    const [owner, setOwner] = useState("");


    let url = params.url + "rooms/"
    if (roomnum)
        url+=roomnum+"/"
    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status == 200) {
                    res.json().then(res => {
                        setHomes(res.homes);
                        if (roomnum) {
                            setLights(res.lights);
                        }
                    })
                }
            });
        }
    }, [url, token,roomnum])


    async function changeState(item,index) {

        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }



    return (
        <>
        <div className="main-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((item, i) => {
                    return (<>
                        <div className={"home-tile "}>
                            <div className="home-container">
                                <p className="home-title">{item.name}</p>
                                {item.owner === user_id && <a className="btn-edit" href={"/" + item.id + "/edit"}><i className="fa fa-gear"></i></a>}
                            </div>
                            <div className="rooms-container">
                                {item.rooms && item.rooms.map((room, i) => {
                                    return (
                                        <div className='room'>
                                            <Link to={`/${room.id}`}>
                                                <p className={"room-title " + (room.id == roomnum ? 'active':"")}>{room.name}</p>
                                            </Link>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>

                    <hr/></>)
                })}

            </div>
            <div className="light-container">
                <div className="lights">
                    {lights && lights.map((item, i) => {
                        return (
                            <div className="tile-btn">
                                <button className={"card btn-"+ (item.state ? "active" : "not-active")} onClick={()=> changeState(item,i)}>
                                    <div className='card-title'>
                                            {item.name}
                                    </div>
                                </button>
                                {owner === user_id && <a className="btn-edit" href={`/${roomnum}/${item.id}/edit`}><i className="fa fa-gear"></i></a>}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
