import { useEffect, useState } from "react";
import { Link, Navigate, useParams } from "react-router-dom";
import { useMessageContext } from "../../context/messageprovider";

import "./style.css"

function Home(params) {
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));
    const { roomnum } = useParams();

    const msgcontext = useMessageContext()

    const [homes, setHomes] = useState([]);
    const [lights, setLights] = useState([]);
    const [owner, setOwner] = useState("");
    const [homeedit, setHomeedit] = useState(-1);
    const [lightedit, setLightedit] = useState(-1);
    const [deletingRoom, setDeletingRoom] = useState(-1);

    let url = params.url + "rooms/"
    if (roomnum)
        url += roomnum + "/"

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {
                    res.json().then(res => {
                        setHomes(res.homes);
                        if (roomnum) {
                            setLights(res.lights);
                        }
                        setOwner(res.owner);
                    })
                }
            });
        }
    }, [url, token,roomnum,homeedit])



    function editHome(id,status) {
        if (status) {
            setHomeedit(id);
        }
        else {
            setHomeedit(-1);
        }
    }

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }

    function handleHomeChange(index, e) {
        let newHomes = [...homes];
        let home = newHomes[index];
        home.name = e.target.value;
        newHomes[index] = home;
        setHomes(newHomes);
    }
    function handleRoomChange(homeIndex, roomIndex, e) {
        let newHomes = [...homes];
        let home = newHomes[homeIndex];
        let room = home.rooms[roomIndex];
        room.name = e.target.value;
        home.rooms[roomIndex] = room;
        newHomes[homeIndex] = home;
        setHomes(newHomes);
    }
    function handleLightChange(index, e) {
        let newlights = [...lights];
        let light = newlights[index];
        light.name = e.target.value;
        newlights[index] = light;
        setLights(newlights);
    }


    async function handleHomeSubmit(event, home) {
        event.preventDefault();
        await fetch(`${params.url}edit/${home.id}`, {
            method: 'POST',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            },
            body: JSON.stringify({ 'home': home })
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.append({ message: res.message, type: (status === 200 ? 'success' : 'error') });
            })
            setHomeedit(-1);
        })
    }

    async function handleLightSubmit(event, light) {

    }

    function handleDeleteRoom(room) {
        setDeletingRoom(room.id);
    }
    function cancelRoomDelete() {
        setDeletingRoom(-1);
    }
    async function confirmRoomDelete(e, room){
        await fetch(`${params.url}delete/room/${room.id}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type': 'application/json'
            }
        }).then(res => {
            const status = res.status;
            res.json().then(res => {

                setDeletingRoom(-1);
                setHomeedit(-1);
                msgcontext.append({ message: res.message, type: status === 200 ? 'success' : 'error' })
            });
        });
    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }

    return (
        <>
            <div className="main-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((home, homeIndex) => {
                    return (<>{homeedit === home.id ?
                        <form className="form-home-edit" onSubmit={(e)=>handleHomeSubmit(e,home)}>
                            <div className="home-container">
                                <input className="input-edit home-title" type={'text'} value={home.name} onChange={(e) => handleHomeChange(homeIndex, e)} />
                                <button className="post-btn"><i className="fa fa-check fa-bigger fa-success"></i></button>
                            </div>
                            <div className="edit-mode room-container">
                                {home.rooms && home.rooms.map((room, roomIndex) => {
                                    return (
                                        deletingRoom === parseInt(room.id) ?
                                            <div className="room delete">
                                                <label>Confirm?</label>
                                                <a className="post-btn" onClick={(e)=>confirmRoomDelete(e,room)}><i className="fa fa-check fa-danger"></i></a>
                                                <a className="post-btn" onClick={cancelRoomDelete}><i className="fa fa-xmark fa-success"></i></a>
                                            </div> :
                                            <div className="room">
                                                <input className="input-edit room-title" type={'text'} value={room.name} onChange={(e) => handleRoomChange(homeIndex, roomIndex, e)} />
                                                <a className="post-btn" onClick={() => handleDeleteRoom(room)}><i className="fa fa-xmark fa-danger"></i></a>
                                            </div>
                                    )
                                })}
                            </div>
                        </form> :
                        <div className={"home-tile "}>
                            <div className="home-container">
                                <p className="home-title">{home.name}</p>
                                {home.owner === user_id && <a className="btn-edit" onClick={() => editHome(home.id, true)}><i className="fa fa-gears fa-primary"></i></a>}
                            </div>
                            <div className="room-container">
                                {home.rooms && home.rooms.map((room, i) => {
                                    return (
                                        <div className='room'>
                                            <Link to={`/${room.id}`}>
                                                <p className={"room-title " + (parseInt(room.id) === roomnum ? 'active' : "")}>{room.name}</p>
                                            </Link>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    }
                    <hr/></>)
                })}

            </div>
            <div className="light-tile">
                <div className="lights">
                    {lights && lights.map((light, i) => {
                        return (lightedit === light.id ?
                            <form >
                                <div className="light-container">
                                    <input className="input-edit room-title" type={'text'} value={light.name} onChange={(e)=>handleLightChange(i,e)}/>
                                </div>
                            </form>
                            :
                            <div className="light-container">
                                <button className={"light-title light-" + (light.state ? "active" : "not-active")} onClick={() => changeState(light, i)}><span>{light.name}</span><i className={"fa-lightbulb fa " + (light.state ? "fa-solid fa-success" : "fa-regular fa-danger")}></i></button>
                                {owner === user_id && <a className="btn-edit" href={`/${roomnum}/${light.id}/edit`}><i className="fa fa-gear fa-primary"></i></a>}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
