// import logo from './logo.svg';
import './App.css';
import { Container } from 'react-bootstrap';
import NavBar from './components/navbar/Navbar';

import { BrowserRouter, Route, Routes} from "react-router-dom";
import {useEffect, useState } from 'react';
import UserProvider from './context/user';



function App() {
  const [fetching, setfetching] = useState(false);

  const [data, setdata] = useState([])

  const load = async (url,statemethod, method = 'GET', body = {} ) => {
      setfetching(true);
      await fetch(url, {
        Method: method,
        Body: body,
      })
        .then(res => res.json()).then(res => {
          statemethod(res)
          // console.log(res)
        })
        .catch(err => {
          console.log(err);
      })
      setfetching(false);
    }

  useEffect(() => {

    load('http://localhost:8000/api/all/?format=json');
  },[])

  const asyncaller = { 'load': load, 'state': fetching, 'set' : setfetching}

  return (
    <BrowserRouter>

      <div className="App">
      <UserProvider>

            <NavBar />
            <Container>
            <div>
              {!fetching && data.map((item, i) => {
                console.log(item.id);
                return (<p key="{item.id}">{item.name}</p>)
              })
              }
            </div>
            <Routes>
              <Route index element={<div></div> } />
              <Route path='login' element={<div></div> } />
              <Route path='signup' element={<div></div> } />
              <Route path='login' element={<div></div> } />
              <Route path='login' element={<div></div> } />
            </Routes>
          </Container>
          </UserProvider>

          </div>
        </BrowserRouter>
  );
}

export default App;
