import { useEffect, useState } from "react";
import { Navigate, useParams } from "react-router-dom";


function Edit(params) {
    const { hid, rid, lid } = useParams();
    const [element, setElement] = useState({});
    const token = localStorage.getItem('access_token');
    let url = params.url;
    if (lid) {
        url += 'light/' + lid + "/";
    }
    else if (rid) {
        url += 'room/' + rid + "/";
    }
    else if (hid) {
        url += 'home/' + hid + "/";
    }

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status === 200) {

                    res = res.json()

                }
            }).then(data => {
                console.log(data);
            })
        }
    });
    if (!token) {
        return <Navigate to='login'/>
    }
    return (<div>
        <h1>Do You really Want to Edit {element && element.name} ??</h1>
    </div>)
}

export default Edit;
