import { useEffect, useState } from "react";
import { Link, Navigate, redirect, useParams } from "react-router-dom";
import { useMessageContext } from "../../context/messageprovider";

import "./style.css"

function Home(params) {
    const token = localStorage.getItem('access_token');
    const user_id = parseInt(localStorage.getItem('user_id'));
    const { roomnum } = useParams();

    const msgcontext = useMessageContext()

    const [homes, setHomes] = useState([]);
    const [lights, setLights] = useState([]);
    const [owner, setOwner] = useState("");
    const [homeedit, setHomeedit] = useState(-1);

    let url = params.url + "rooms/"
    if (roomnum)
        url += roomnum + "/"

    useEffect(() => {
        if (token) {
            fetch(url, {
                method: "GET",
                headers: {
                    Authorization: token
                }
            }).then(res => {
                if (res.status == 200) {
                    res.json().then(res => {
                        setHomes(res.homes);
                        if (roomnum) {
                            setLights(res.lights);
                        }
                        setOwner(res.owner);
                    })
                }
            });
        }
    }, [url, token,roomnum])


    function editHome(id,status) {
        if (status) {
            setHomeedit(id);
        }
        else {
            setHomeedit(-1);
        }
    }

    async function changeState(item,index) {
        await fetch(`${params.url}set/${item.id}/${item.state ? 0 : 1}`, {
            method: 'GET',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            }
        }).then(res => res.json()).then(res => {
            const newlights = res.lights;
            setLights(newlights);
        })
    }
    function handleHomeChange(index, e) {
        let newHomes = [...homes];
        let home = newHomes[index];
        home.name = e.target.value;
        newHomes[index] = home;
        setHomes(newHomes);
    }
    function handleRoomChange(homeIndex, roomIndex, e) {
        let newHomes = [...homes];
        let home = newHomes[homeIndex];
        let room = home.rooms[roomIndex];
        room.name = e.target.value;
        home.rooms[roomIndex] = room;
        newHomes[homeIndex] = home;
        setHomes(newHomes);
    }
    async function handleSubmit(event, home) {
        event.preventDefault();
        await fetch(`${params.url}edit/${home.id}`, {
            method: 'POST',
            headers: {
                Authorization: token,
                'Content-type':'application/json'
            },
            body: JSON.stringify({ 'home': home })
        }).then(res => {
            const status = res.status;
            res.json().then(res => {
                msgcontext.set([...msgcontext.messages, { message: res.message, type: (status === 200 ? 'success' : 'error') }]);
            })
            setHomeedit(-1);
        })
    }

    function handleDeleteRoom(e,room) {
        console.log(room);
        const parent = e.currentTarget.parentNode;

        parent.innerHTML = `
            <label>Confirm?</label>
            <a className="post-btn" onClick={(e)=>confirmRoomDelete(e,room)}><i className="fa fa-check fa-danger"></i></a>
            <a className="post-btn" onClick={(e) => cancelRoomDelete(e, room)}><i className="fa fa-xmark fa-success"></i></a>`
            parent.classList.add('delete');
    }

    function cancelRoomDelete(e, room) {
        const parent = e.currentTarget.parentNode;
        parent.innerHTML = `
            <input className="input-edit room-title" type={'text'} value={room.name} onChange={(e) => handleRoomChange(homeIndex, roomIndex, e)} />
            <a className="post-btn" onClick={(e)=>handleDeleteRoom(e,room)}><i className="fa fa-xmark fa-danger"></i></a>`
        parent.classList.remove('delete');
    }

    async function confirmRoomDelete(e, room){

    }


    if (!token) {
        return (
            <Navigate to='login'/>
        )
    }

    return (
        <>
            <div className="main-container">
            <div className="homes">
                {/* <h1>Available Homes</h1> */}
                {homes && homes.map((home, homeIndex) => {
                    return (<>{homeedit === home.id ?
                        <form className="form-home-edit" onSubmit={(e)=>handleSubmit(e,home)}>
                            <div className="home-container">
                                <input className="input-edit home-title" type={'text'} value={home.name} onChange={(e) => handleHomeChange(homeIndex, e)} />
                                <button className="post-btn"><i className="fa fa-check fa-bigger fa-success"></i></button>
                            </div>
                            <div className="edit-mode room-container">
                                {home.rooms && home.rooms.map((room, roomIndex) => {
                                    return (
                                        // <div className="room">
                                        //     <input className="input-edit room-title" type={'text'} value={room.name} onChange={(e) => handleRoomChange(homeIndex, roomIndex, e)} />
                                        //     <a className="post-btn" onClick={(e)=>handleDeleteRoom(e,room)}><i className="fa fa-xmark fa-danger"></i></a>
                                        // </div>
                                        <div className="delete room">
                                            <label>Confirm?</label>
                                            <a className="post-btn" onClick={(e)=>confirmRoomDelete(e,room)}><i className="fa fa-check fa-danger"></i></a>
                                            <a className="post-btn" onClick={(e) => cancelRoomDelete(e, room)}><i className="fa fa-xmark fa-success"></i></a>
                                        </div>
                                    )
                                })}
                            </div>
                        </form> :
                        <div className={"home-tile "}>
                            <div className="home-container">
                                <p className="home-title">{home.name}</p>
                                {home.owner === user_id && <a className="btn-edit" onClick={() => editHome(home.id, true)}><i className="fa fa-gears fa-primary"></i></a>}
                            </div>
                            <div className="room-container">
                                {home.rooms && home.rooms.map((room, i) => {
                                    return (
                                        <div className='room'>
                                            <Link to={`/${room.id}`}>
                                                <p className={"room-title " + (room.id == roomnum ? 'active' : "")}>{room.name}</p>
                                            </Link>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    }

                    <hr/></>)
                })}

            </div>
            <div className="light-container">
                <div className="lights">
                    {lights && lights.map((item, i) => {
                        return (
                            <div >
                                <button className={"hello"+ (item.state ? "active" : "not-active")} onClick={()=> changeState(item,i)}>
                                    <div className='card-title'>
                                            {item.name}
                                    </div>
                                </button>
                                {owner === user_id && <a className="btn-edit" href={`/${roomnum}/${item.id}/edit`}><i className="fa fa-gear"></i></a>}
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
        </>
    );
}

export default Home;
